package main

import \(
	"fmt"
	toon "github.com/toon-format/toon-go"
	"strings"
\)

type wrapper struct {
	V string `toon:"v"`
}

func escapeField\(s string\) string {
	if !strings.ContainsAny\(s, ",\\"\\n\\\\:[]{}"\) {
		return s
	}
	out, err := toon.MarshalString\(wrapper{V: s}\)
	if err != nil {
		return s
	}
	return strings.TrimPrefix\(out, "v: "\)
}

func main\(\) {
	// Test various special chars
	tests := []string{
		"Hello, World",
		"Has \\"quotes\\"",
		"Has\\nnewlines",
		"Has\\\\backslash",
		"Normal title",
		"Has: colon",
		"Has [brackets]",
		"Has {braces}",
	}
	for _, t := range tests {
		fmt.Printf\("%-25s -> %s\\n", t, escapeField\(t\)\)
	}

	// Also test what MarshalString produces for lists
	type row struct {
		ID    string `toon:"id"`
		Title string `toon:"title"`
		Status string `toon:"status"`
		Priority int  `toon:"priority"`
	}
	type listWrapper struct {
		Tasks []row `toon:"tasks"`
	}

	data := listWrapper{Tasks: []row{
		{ID: "tick-a1b2", Title: "Setup Sanctum", Status: "done", Priority: 1},
		{ID: "tick-c3d4", Title: "Login endpoint", Status: "open", Priority: 1},
	}}
	out, _ := toon.MarshalString\(data, toon.WithIndent\(2\)\)
	fmt.Println\("\\n--- MarshalString for list ---"\)
	fmt.Println\(out\)
}
EOF)

Bash(/tmp/claude/test_escape2.go << 'EOF'
package main

import \(
	"fmt"
	toon "github.com/toon-format/toon-go"
\)

func main\(\) {
	type row struct {
		ID    string `toon:"id"`
		Title string `toon:"title"`
		Status string `toon:"status"`
		Priority int  `toon:"priority"`
	}
	type listWrapper struct {
		Tasks []row `toon:"tasks"`
	}

	// Test with comma in title - does toon-go escape it?
	data := listWrapper{Tasks: []row{
		{ID: "tick-a1b2", Title: "Hello, World", Status: "open", Priority: 1},
	}}
	out, _ := toon.MarshalString\(data, toon.WithIndent\(2\)\)
	fmt.Println\("--- Comma title ---"\)
	fmt.Println\(out\)

	// Test with trailing newline from MarshalString
	fmt.Printf\("Ends with newline: %v\\n", out[len\(out\)-1] == '\\n'\)
}
EOF)
